import React, {Component} from 'react';
import logo from './logo.svg';
import './App.css';

const People = ({people}) => {
  if (people.results) {
    return (
        <div>
            <center><h1>Contact List</h1></center>
            {people.results.map((character) => (
                <div class="card">
                    <div class="card-body">
                        <h5 class="card-title">{character.name}</h5>
                        <h6 class="card-subtitle mb-2 text-muted">{character.height}</h6>

                    </div>
                </div>
            ))}
        </div>
    )
  } else return null;
};

function PaginatorButton(props) {
  return (
    <button onClick="nextPage({this.props.url})"> 
      > 
    </button>
  );
}

//const Paginator = ({people}) => {
class Paginator extends Component {
  constructor(props) {
    super(props);
  }

  render() {
    const next = this.props.people.next ? <PaginatorButton url={this.props.people.next}/> : "";
    return (
      <div>
        <People people={this.props.people} />
        {next}
      </div>
    );
  }
}

class App extends Component {

  state = {
    people: []
  }

  componentDidMount() {
        fetch('http://swapi.dev/api/people/')
        .then(res => res.json())
        .then((data) => {
          this.setState({ people: data })
        })
        .catch(console.log)
  }

  render() {
    return (
      <Paginator people={this.state.people}/>
    );
  }
}

export default App;
