  fetch(url)
    .then(res => res.json())
    .then((data) => {
      return data;
    })
}*/

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
        people: [],
        selected: [null, null],
        loading: true
    }
  }

  componentDidMount() {
    fetch('http://swapi.dev/api/people/')
      .then(res => res.json())
      .then((data) => {
        this.setState({ 
          people: data,
          loading: false
        })
      })
  }

  handleChangePage = (url) => {
    this.setState({
      loading: true
    });

    fetch(url)
      .then(res => res.json())
      .then((data) => {
        this.setState({ 
          people: data,
          loading: false
        })
      })
  }

  handleSelectCharacter = (name, url) => {
    this.setState({
      selected: [name, url]
    });
  }

  handleCloseCharacter = () => {
    this.setState({
      selected: [null, null]
    })
  }

  render() {
    return (
      <div>
        <Header />

        <div className="flex-container">
          <Paginator loading={this.state.loading} people={this.state.people} characterSelected={this.state.selected[0]} onChangePage={this.handleChangePage} onSelectCharacter={this.handleSelectCharacter} />

          {this.state.selected[1] && 
            <Character url={this.state.selected[1]} onClose={this.handleCloseCharacter} />}
        </div>
      </div>
    );
  }
}

export default App;
