    super(props);
  }
}

class App extends Component {
  state = {
      people: [],
      personSelected: null
  }

  componentDidMount() {
    fetch('http://swapi.dev/api/people/')
    .then(res => res.json())
    .then((data) => {
      this.setState({ people: data })
    })
    .catch(console.log)
  }

  handleChangePage = (url) => {
    fetch(url)
      .then(res => res.json())
      .then((data) => {
        this.setState({ people: data })
      })
      .catch(console.log)
  }

  render() {
    return (
      <Paginator people={this.state.people} onChangePage={this.handleChangePage} onSelectPerson={this.handleSelectPerson}/>
      //{this.state.personSelected}
    );
  }
}

export default App;
