import React, {Component} from 'react';
import logo from './logo.svg';
import './App.css';

const People = ({people}) => {
  if (people.results) {
    return (
        <div>
            <center><h1>Contact List</h1></center>
            {people.results.map((character) => (
                <div class="card">
                    <div class="card-body">
                        <h5 class="card-title">{character.name}</h5>
                        <h6 class="card-subtitle mb-2 text-muted">{character.height}</h6>

                    </div>
                </div>
            ))}
        </div>
    )
  } else return null;
};

function PaginatorButton(props) {
  const direction = props.next ? '>' : '<';
  return (
    <button onClick={this.props.changePage(this.props.url)}> 
      {direction} 
    </button>
  );
}

//const Paginator = ({people}) => {
class Paginator extends Component {
  constructor(props) {
    super(props);
  }

  changePage() {
    alert(1);
  }

  render() {
    const data = this.props.people;
    const previous = data.previous ? <PaginatorButton url={data.previous}  /> : "";
    const next = data.next ? <PaginatorButton url={data.next} next={1} /> : "";

    return (
      <div>
        <People people={data} />
        {previous}
        {next}
      </div>
    );
  }
}

class App extends Component {

  state = {
    people: []
  }

  componentDidMount() {
    fetch('http://swapi.dev/api/people/')
    .then(res => res.json())
    .then((data) => {
      this.setState({ people: data })
    })
    .catch(console.log)
  }

  handleChangePage(url) {
    fetch(url)
      .then(res => res.json())
      .then((data) => {
        this.setState({ people: data })
      })
      .catch(console.log)
  }

  render() {
    return (
      <Paginator people={this.state.people}/>
    );
  }
}

export default App;
