import React, {Component} from 'react';
import './App.css';

const Header = () => {
  return (
    <div>
      <h1>STAR FOLKS</h1>
      <h3>Click on someone to see more</h3>
    </div>
  );
}

const Person = ({name, url, selected, onclickhandler}) => {
  return (
    // <div className={"character " + (selected ? "character-selected" : "")} onClick={() => onclickhandler(name, url)}>
    <div className={"character " + (selected ? "character-selected" : "")} onClick={() => onclickhandler(name, url)}>
      {name}
    </div>
  );
}

const People = ({people, selected, onSelectCharacter}) => {
  if (people.results) {
    return (
        <div>
            {people.results.map((character) => (
                <Person key={character.name} name={character.name} url={character.url} selected={character.name === selected ? true : false} onclickhandler={onSelectCharacter} />
            ))}
        </div>
    )
  } else return null;
};

//class PaginatorButton extends Component {
function PaginatorButton(props) {
    const direction = props.next ? '>' : '<';

    return (
      <button className="button-paginator" onClick={() => props.onChangePage(props.url)}> 
        {direction} 
      </button>
    );  
}

class Paginator extends Component {
  constructor(props) {
    super(props);
  }

  handleSelectCharacter= (name, url) => {
    this.props.onSelectCharacter(name, url);
  }

  render() {
    const data = this.props.people;
    const character = this.props.characterSelected;
    const previous = data.previous ? <PaginatorButton url={data.previous} onChangePage={(url) => this.props.onChangePage(url)} /> : "";
    const next = data.next ? <PaginatorButton url={data.next} next={1} onChangePage={(url) => this.props.onChangePage(url)} /> : "";

    const content = this.props.loading ? <div className="loader"/> : <People people={data} selected={character} onSelectCharacter={this.handleSelectCharacter} />;

    return (
      <div className="paginator">
        {content}

        <div className="paginator-buttons">
          {previous}
          {next}
        </div>
      </div>
    );
  }
}

const CloseButton = ({onclickhandler}) => {
  return (
    <div className={"button-close"} onClick={onclickhandler}>
      Close
    </div>
  );
}

const CharacterHeader = ({name, height, mass}) => {
  const mass_str = mass === "unknown" ? "unknown weight" : mass + "kg"
  const height_str = height === "unknown" ? "unknown height" : height + "cm"

  return (
    <div className="character-header">
      {name}
      <div>
        {height_str}, {mass_str}
      </div>
    </div>
  );
}

const CharacterBody = ({data, onclickhandler}) => {
  /*const film_buttons = "";
  const films = data.films;
  for (let i=0; i<films.length; i++) {
    <button className="button" onClick={onclickhandler}>Films</button>
  }*/

  return (
    <div className="character-body">
      <p>Skin</p> {data.skin_color}
      <p>Hair</p> {data.hair_color}
      <p>Eyes</p> {data.eye_color}

      <p>Films</p> 
      {data.films.map((film) => (
          <button className="button" onClick={() => onclickhandler(film)}>Films</button>
      ))}
    </div>
  );
}

class Character extends Component {
  constructor(props) {
    super(props);
    this.state = {
      character: null,
      loading: false
    }
  }

  componentDidMount() {
    this.setState({
      loading: true
    })

    fetch(this.props.url)
      .then(res => res.json())
      .then((data) => {
        this.setState({
          character: data,
          loading: false
        })
      })
  }
  componentDidUpdate(prevProps, prevState) {
    /*this.setState({
      loading: true
    })*/

    if (prevState.character !== this.state.character) {
      fetch(this.props.url)
        .then(res => res.json())
        .then((data) => {
          this.setState({
            character: data,
            loading: false
          })
        })
    }
  }

  handleClickFilms(url) {
    alert(url);
    /*fetch(this.props.url)
      .then(res => res.json())
      .then((data) => {
        this.setState({
          films: data
        })
      })*/
  }

  render() {
    const data = this.state.character;

    /*if (this.state.character) {
      return (
        <div className="more">
          <CharacterHeader name={data.name} height={data.height} mass={data.mass} />

          <CloseButton onclickhandler={this.props.onClose} />
        </div>
      );
    } else return null;*/

    // if (this.state.loading) {
    //   return (
    //     <div className="more"> 
    //       <div className="loader"/>
    //     </div>
    //   );
    // }
    if (data) {
      return (
        <div className="more"> 
          load :{this.state.loading}
          <CharacterHeader name={data.name} height={data.height} mass={data.mass} />
          <CharacterBody data={data} onclickhandler={this.handleClickFilms}/>
          <CloseButton onclickhandler={this.props.onClose} />
        </div>
      );
    }
    else return null;
    //} else return <div className="loader" />;
  }

}

/*function fetch (url) {
  fetch(url)
    .then(res => res.json())
    .then((data) => {
      return data;
    })
}*/

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
        people: [],
        selected: [null, null],
        loading: true
    }
  }

  componentDidMount() {
    fetch('http://swapi.dev/api/people/')
      .then(res => res.json())
      .then((data) => {
        this.setState({ 
          people: data,
          loading: false
        })
      })
  }

  handleChangePage = (url) => {
    this.setState({
      loading: true
    });

    fetch(url)
      .then(res => res.json())
      .then((data) => {
        this.setState({ 
          people: data,
          loading: false
        })
      })
  }

  handleSelectCharacter = (name, url) => {
    this.setState({
      selected: [name, url]
    });
  }

  handleCloseCharacter = () => {
    this.setState({
      selected: [null, null]
    })
  }

  render() {
    return (
      <div>
        <Header />

        <div className="flex-container">
          <Paginator loading={this.state.loading} people={this.state.people} characterSelected={this.state.selected[0]} onChangePage={this.handleChangePage} onSelectCharacter={this.handleSelectCharacter} />

          {this.state.selected[1] && 
            <Character url={this.state.selected[1]} onClose={this.handleCloseCharacter} />}
        </div>
      </div>
    );
  }
}

export default App;
