import React, {Component} from 'react';
import logo from './logo.svg';
import './App.css';

const People = ({people, onShowCharacter}) => {
  if (people.results) {
    return (
        <div>
            <center><h1>Contact List</h1></center>
            {people.results.map((character) => (
                <div onClick={() => onShowCharacter(character.url)}>
                    <h5>{character.name}</h5>
                </div>
            ))}
        </div>
    )
  } else return null;
};

//function PaginatorButton(props) {
class PaginatorButton extends Component {
  render() {
    const direction = this.props.next ? '>' : '<';

    return (
      <button onClick={() => this.props.onChangePage(this.props.url)}> 
        {direction} 
      </button>
    );
  }
}

//const Paginator = ({people}) => {
class Paginator extends Component {
  constructor(props) {
    super(props);
  }

  showCharacter(url) {
    alert(url);
  }

  render() {
    const data = this.props.people;
    const previous = data.previous ? <PaginatorButton url={data.previous} onChangePage={(url) => this.props.onChangePage(url)} /> : "";
    const next = data.next ? <PaginatorButton url={data.next} next={1} onChangePage={(url) => this.props.onChangePage(url)} /> : "";

    return (
      <div>
        <People people={data} onShowCharacter={this.showCharacter} />
        {previous}
        {next}
      </div>
    );
  }
}

class App extends Component {
  state = {
      people: []
  }

  componentDidMount() {
    fetch('http://swapi.dev/api/people/')
    .then(res => res.json())
    .then((data) => {
      this.setState({ people: data })
    })
    .catch(console.log)
  }

  handleChangePage = (url) => {
    fetch(url)
      .then(res => res.json())
      .then((data) => {
        this.setState({ people: data })
      })
      .catch(console.log)
  }

  render() {
    return (
      <Paginator people={this.state.people} onChangePage={this.handleChangePage}/>
    );
  }
}

export default App;
