import React, {Component} from 'react';
import logo from './logo.svg';
import './App.css';

const People = ({people, selected, onSelectCharacter}) => {
  if (people.results) {
    return (
        <div>
            {people.results.map((character) => (
                <div className={character.name === selected ? "char-selected" : "char"} onClick={() => onSelectCharacter(character.name, character.url)}>
                    <h5>{character.name}</h5>
                </div>
            ))}
        </div>
    )
  } else return null;
};

//function PaginatorButton(props) {
class PaginatorButton extends Component {
  render() {
    const direction = this.props.next ? '>' : '<';

    return (
      <button onClick={() => this.props.onChangePage(this.props.url)}> 
        {direction} 
      </button>
    );
  }
}

//const Paginator = ({people}) => {
class Paginator extends Component {
  constructor(props) {
    super(props);
  }

  handleSelectCharacter= (name, url) => {
    this.props.onSelectCharacter(name, url);
  }

  render() {
    const data = this.props.people;
    const character = this.props.characterSelected;
    const previous = data.previous ? <PaginatorButton url={data.previous} onChangePage={(url) => this.props.onChangePage(url)} /> : "";
    const next = data.next ? <PaginatorButton url={data.next} next={1} onChangePage={(url) => this.props.onChangePage(url)} /> : "";

    return (
      <div>
        <People people={data} characterSelected={character} onSelectCharacter={this.handleSelectCharacter} />
        {previous}
        {next}
      </div>
    );
  }
}

class Character extends Component {
  constructor(props) {
    super(props);
  }

  // render() {
  //   return (
  //     {a}
  //   );
  // }

}

class App extends Component {
  state = {
      people: [],
      characterSelected: null,
      url: null
  }

  componentDidMount() {
    fetch('http://swapi.dev/api/people/')
      .then(res => res.json())
      .then((data) => {
        this.setState({ people: data })
      })
      .catch(console.log)
  }

  handleChangePage = (url) => {
    fetch(url)
      .then(res => res.json())
      .then((data) => {
        this.setState({ people: data })
      })
      .catch(console.log)
  }

  handleSelectCharacter = (name, url) => {
    this.setState({
      characterSelected: name,
      url: url
    });
  }

  render() {
    return (
      <Paginator people={this.state.people} characterSelected={this.state.characterSelected} onChangePage={this.handleChangePage} onSelectCharacter={this.handleSelectCharacter}/>
      //{this.state.personSelected}
    );
  }
}

export default App;
